INSERT INTO `agcms_services_i` (`ID`, `TITLE`, `TITLE2`, `ALIAS`, `PARENT`, `LEFT_CONTENT`, `RIGHT_CONTENT`, `META_TITLE`, `META_DESC`, `META_KEYWORDS`, `TAGS`, `PHOTO1`, `PHOTO2`) VALUES
(1, 'США', 'США', 'usa', ',1,', '<p style=\"text-align: justify;\"><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Чем раньше студент задумается о поступлении в медицинский университет - тем большую поддержку мы можем ему оказать. В идеале начинать работу сразу после GCSE - это даст возможность выбрать &laquo;правильную&raquo; школу/колледж на A-levels / IB, где учителя действительно имеют успешные кейсы, а значит сэкономить время и деньги на репетиторов и консультантов. Ведь не смотря на высокую стоимость обучения, сложность и продолжительность образовательного процесса в медицинских ВУЗах Англии - это по-прежнему самая популярная специальность, как среди местных студентов, так и среди иностранных.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Alpha Capital Education</strong>&nbsp;имеет опыт работы с абитуриентами, желающими поступить на медицинский факультет в Англии - наши русскоговорящие консультанты сами обучились в топовых Университетах Англии (Imperial College London) и в данный момент являются сотрудниками государственных госпиталей Англии.</span></p>', '<p style=\"text-align: justify;\"><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Оценив сильные и слабые стороны студента мы сможем выстроить стратегию поступления. Например, Brighton University и Southampton University больше упираются на опыт в медицинской сфере и благотворительную работу. Imperial College London на академические достижения. Edinburgh University на extracurriculars. Все эти факторы должны учитываться при поступлении.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Стандартный набор услуг, который выбирают наши клиенты:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">1. Подбор ВУЗов</span><br /><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">2. Правка мотивационного письма </span><br /><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">3. Подготовка к интервью </span><br /><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">4. Помощь с выбором альтернативных ВУЗов в Европе и США на случай необходимости </span><br /><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">5. Подбор курсов BMAT/UKCAT при необходимости</span></p>', '', '', '', '', 'students-in-balliol-college-oxford-england-948400550_4896x3264-e1539962570710-880x470.jpg', '2018-0328_fountainacademicquad-03.jpg'),
